
$("#question-form").submit(function(d){d.preventDefault();var c=$("#question-email").val(),b=$("#question-name").val(),a=$("#question-text").val();$.ajax({type:"POST",url:"/question",data:"userEmail="+c+"&userName="+b+"&question="+a,dataType:"html",dataFilter:function(e){return $(e).filter("#error").html()},success:function(e){if(e==null){$(".aa-question").html("<h2>Спасибо за ваш вопрос, мы ответим Вам на указанную почту</h2>")}else{$("#question-error").show();$("#question-error").html(e)}}})});$("#question-email").focus(function(){$("#question-error").hide()});$("#question-name").focus(function(){$("#question-error").hide()});$("#question-text").focus(function(){$("#question-error").hide()});